javascriptEvents.javascriptLogic:
var req = {};
            var ruleOutput;
            req.personAge = scope.findFormFieldWithId(scope.allFormFields, 'age').value;
            var cookieValue;
            if (document.cookie && document.cookie != '') {
                    var cookies = document.cookie.split(';');
                    for (var i = 0; i < cookies.length; i++) {
                        var cookie = jQuery.trim(cookies[i]);
                        // Does this cookie string begin with the name we want?
                        if (cookie.substring(0, 'CSRF-TOKEN'.length + 1) == ('CSRF-TOKEN' + '=')) {
                            cookieValue = decodeURIComponent(cookie.substring('CSRF-TOKEN'.length + 1));
                            break;
                    }
                }
            }

            $.ajax({
                url: ACTIVITI.CONFIG.contextRoot + '/app/rest/dmn/agerule/evaluate',
                    type: "POST",
                    dataType: "json",
                    contentType: "application/json",
                    data: JSON.stringify(req),
                    async:false,
                    headers: { 'X-CSRF-TOKEN': cookieValue } ,
                    success: function(data){ruleOutput = data.underAge}
                });
            if(ruleOutput===true){
                var newAlert = {message: "Error: The Person is Underage", type: "error"};
                scope.$root.alerts.queue.length = 0
                scope.$root.alerts.queue.push(newAlert);
                scope.$parent.$root.showAlert(scope.$root.alerts)
                return false;
            }
